import React, {useState} from "react"

function Task({ task, loggedInUser, onHandleCompleteTask }) {

    console.log(task)

    const [taskEdit, setTaskEdit] = useState(false)

    function handleCompleteTaskClick() {
        onHandleCompleteTask(task)
    }

    function handleEditTaskClick(task) {
        setTaskEdit((taskEdit) => !taskEdit)
        console.log('hi')
    }

    function handleEditTaskName(e) {
        e.preventDefault()
        console.log(task)
    }

    function handleEditTaskCategory(e) {
        e.preventDefault()
    }

    function handleEditTaskDate(e) {
        e.preventDefault()
    }


    // function handleEditTask(editTask) {
    //     fetch(`http://localhost:9292/tasks/${editTask.id}`, {
    //         method: 'PATCH',
    //         headers: {
    //             'Content-Type': 'application/json'
    //         },
    //         body: JSON.stringify({
    //             name:
    //         })
    //     })
    // }

    return (
        <div className="task-card">
            <p className="singletasks">Task: {taskEdit ? 
            <form onSubmit={handleEditTaskNameSubmit}>
                <input type='text' placeholder={task.name}></input> 
                <button>Add Change</button>
            </form>
            : task.name}</p>
            <p>Category: {taskEdit ? 
            <form onSubmit={handleEditTaskCategorySubmit}>
                <input type='text' placeholder={task.category}
                onChange={handleEditTaskCategory}></input> 
                <button>Add Change</button>
            </form>
            : task.category}</p>
            {task.date ? <p>Due by: {taskEdit ? 
            <form onSubmit={handleEditTaskDateSubmit}>
                <input type='text' placeholder={task.date}></input> 
                <button>Add Change</button>
            </form>
            : task.date}</p> : null}
            <button onClick={handleCompleteTaskClick}>✔️</button>
            <button onClick={handleEditTaskClick}>{taskEdit ? 'Done' : 'Edit'}</button>
            {taskEdit ? <p>hi</p>:null}
            _________________________
        </div>
    )
}

export default Task
