                            <option value='goal'>Goal</option>
                            <option value='reminder'>Reminder</option>
                        </select>
                    </p>
                    <p>
                        <input type='date' ></input>

                    </p>
                    <p>
                        <button>Add Task</button>
                    </p>

                </form>
            </div>
                : <button onClick={handleNewTask}>New Task</button>}

            <div className="tasklist">

            </div>
        </div>
    )
}

export default Tasks
